// (c) Kallol Borah, 2020
// Implements sub accounts linked to a specific general ledger accounts

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "../interfaces/VerifiedEntry.sol";
import "../interfaces/VerifiedSystem.sol";
import "../interfaces/VerifiedAccount.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";

contract Account is VerifiedAccount, Initializable{

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    VerifiedSystem private factory;

    bytes32 public accountName;

    address accountCreator;

    address accountHolder;

    address ledger;

    bytes32 currency;    

    // mapping each account holder to multiple accounts
    mapping(address => mapping(uint256 => VerifiedEntry.account)) transactions;

    //emitting post account entry event
    event postAccountEntry(
                        address accountAddress,
                        bytes32 accountName,
                        uint256 txDate,
                        address party,
                        bytes32 txType,
                        bytes32 txDescription,
                        bytes32 currency,
                        int256 txAmount,
                        bytes32 voucherType
                    );

    // initiliaze proxies
    function initialize(bytes32 _accountName, address _ledger, address _owner, address _accountCreator, address _accountHolder, bytes32 _currency) public initializer{
        factory = VerifiedSystem(_owner);
        accountName = _accountName;
        ledger = _ledger;
        accountCreator = _accountCreator;
        accountHolder = _accountHolder;
        currency = _currency;
    }

    // create accounting entry for a transaction
    function postEntry(address _party, int256 _txAmount, bytes32 _txType, uint256 _txDate, bytes32 _txDescription, bytes32 _vchType) external{
        //only clients can post entries
        require(accountCreator==msg.sender);
        // amount should not be zero
        require(_txAmount !=0);
        // transaction type should be credit or debit
        require(_txType =="Credit" || _txType == "Debit");
        // check for allowed voucher types
        require(_vchType=="Journal" || _vchType=="Cash" || _vchType=="Bank" || _vchType=="Purchase" || _vchType=="Sale");
        // check if counter party exists
        uint256 i =0;
        if(factory.confirmAccountHolder(_party)){
            bool found = false;
            while(true){
                if(transactions[accountHolder][i].party==address(0x0) && !found){
                    transactions[accountHolder][i].txCurrency = currency;
                    transactions[accountHolder][i].party = _party;
                    transactions[accountHolder][i].txAmount = ABDKMathQuad.fromInt(_txAmount);
                    transactions[accountHolder][i].txType = _txType;
                    transactions[accountHolder][i].txDate = _txDate;
                    transactions[accountHolder][i].txDescription = _txDescription;
                    transactions[accountHolder][i].voucherType = _vchType;
                    emit postAccountEntry(
                        address(this),
                        accountName,
                        _txDate,
                        _party,
                        _txType,
                        _txDescription,
                        currency,
                        _txAmount,
                        _vchType
                    );
                    found = true;
                    break;
                }
                i=i+1;
            }
        }
    }

    // get transaction entry for a given account number and date
    function getEntries(uint256 _txDate) external view returns(VerifiedEntry.account[] memory){
        //only clients can get entries
        require(ledger==msg.sender);
        // transaction date should be specified
        require(_txDate != 0);
        uint256 i = 0;
        uint256 _entries = 0;
        while(transactions[accountHolder][i].party!=address(0x0) && transactions[accountHolder][i].txDate>=_txDate){
            i = i + 1;
            _entries = i;
        }
        VerifiedEntry.account[] memory accountEntries = new VerifiedEntry.account[](_entries);
        i = 0;        
        uint256 count = 0;
        for(i=0; count<_entries; i++){
            if(transactions[accountHolder][i].party!=address(0x0) && transactions[accountHolder][i].txDate>=_txDate){
                VerifiedEntry.account memory accountEntry = VerifiedEntry.account({
                    txType : transactions[accountHolder][i].txType,
                    txDate : transactions[accountHolder][i].txDate,
                    txDescription : transactions[accountHolder][i].txDescription,
                    voucherType : transactions[accountHolder][i].voucherType,
                    txCurrency : transactions[accountHolder][i].txCurrency,
                    txAmount : transactions[accountHolder][i].txAmount,
                    party : transactions[accountHolder][i].party
                });
                accountEntries[count] = accountEntry;
                count = count + 1;                
            }
        }
        return accountEntries;
    }

    function getBalance(address _accountHolder) external view returns(bytes16){
        //only parent ledgers can get balance
        require(ledger==msg.sender);
        bytes16 _txAmount = 0;
        uint256 i = 0;
        while(transactions[_accountHolder][i].party!=address(0x0)){
            _txAmount = ABDKMathQuad.add(_txAmount, transactions[_accountHolder][i].txAmount);
            i=i+1;
        }
        return _txAmount;
    }

}
