pragma solidity 0.7.1;
pragma experimental ABIEncoderV2;

import '@balancer-labs/v2-solidity-utils/contracts/openzeppelin/IERC20.sol';

interface ICash is IERC20{

    struct token{
        bytes32 tokenName;
        address tokenAddress;
    }

    function name() external view returns (string memory); 

    function supportTokens(address _token, bytes32 _name) external;

    function checkSupportForToken(address _token) external view returns(bool);

    function getSupportedTokens() external view returns(token[] memory);

    function requestIssue(address _token, uint256 _amount, address _buyer) external;

    function redeemDeposits(uint256 amount, address redeemer, bytes32 currency, uint256 redeemedFor, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external;

    function addIssuedBalance(uint256 amount, address buyer, bytes32 currency, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external;

    function transferIssuedBalance(address transferor, bytes32 currency, uint256 amount, uint256 deposit, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external;

    function transferDeposit(address transferor, uint256 amount, address transferee, bytes32 currency, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external;
    
    function setSigner(address _signer) external;

}
