    function withdraw(address security, address currency, uint256 amount) override external onlyTrader(msg.sender){
        bytes32 poolId = poolSecurity[security];
        for(uint256 i=0; i<positions[security][currency].length; i++){
            IMarginManager.tradePosition memory position = positions[security][currency][i];
            if(position.buyer==msg.sender && collateralBalance[position.buyer][currency]>0){
                if(collateralBalance[position.buyer][currency]>=int(amount)){
                    IERC20(currency).transfer(position.buyer, uint(collateralBalance[position.buyer][currency]));
                    collateralAmount[poolId][currency] = Math.sub(collateralAmount[poolId][currency], uint(collateralBalance[position.buyer][currency]));
                    emit collateral("withdrawl", position.buyer, currency, collateralBalance[position.buyer][currency]);
                    amount = Math.sub(amount, uint(collateralBalance[position.buyer][currency]));
                    collateralBalance[position.buyer][currency] = 0;                    
                }
                else{
                    IERC20(currency).transfer(position.buyer, amount);
                    collateralAmount[poolId][currency] = Math.sub(collateralAmount[poolId][currency], amount);   
                    emit collateral("withdrawl", position.buyer, currency, int(amount));                 
                    collateralBalance[position.buyer][currency] = Math.sub(collateralBalance[position.buyer][currency], int(amount)); 
                    amount = 0;
                }                
                //delete positions[security][currency][orders[position.orderReference]];
            }
            else if(position.seller==msg.sender && collateralBalance[position.seller][currency]>0){
                if(collateralBalance[position.seller][currency]>=int(amount)){
                    IERC20(currency).transfer(position.seller, uint(collateralBalance[position.seller][currency]));
                    collateralAmount[poolId][currency] = Math.sub(collateralAmount[poolId][currency], uint(collateralBalance[position.seller][currency]));
                    emit collateral("withdrawl", position.seller, currency, collateralBalance[position.seller][currency]);
                    amount = Math.sub(amount, uint(collateralBalance[position.seller][currency]));
                    collateralBalance[position.seller][currency] = 0;                    
                }
                else{
                    IERC20(currency).transfer(position.seller, amount);
                    collateralAmount[poolId][currency] = Math.sub(collateralAmount[poolId][currency], amount);   
                    emit collateral("withdrawl", position.seller, currency, int(amount));                 
                    collateralBalance[position.seller][currency] = Math.sub(collateralBalance[position.seller][currency], int(amount)); 
                    amount = 0;
                }
                //delete positions[security][currency][orders[position.orderReference]];
            }
        }
    }

    function getTradingFeeCollected(address _token) override external returns(uint256){
        require(msg.sender==_distribution, "Unauthorized collection request");
        uint256 feeCollected = fees[_token];
        fees[_token] = Math.sub(fees[_token], feeCollected);
        IERC20(_token).transfer(_distribution, feeCollected);
        emit feecollection(address(this), feeCollected, block.timestamp);
        return feeCollected;
    }  

    /**
     * @dev This helper function is a fast and cheap way to convert between IERC20[] and IAsset[] types
     */
    function _convertERC20sToAssets(IERC20[] memory tokens) internal pure returns (IAsset[] memory assets) {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            assets := tokens
        }
    }

    /**
     * @dev Returns a scaling factor that, when multiplied to a token amount for `token`, normalizes its balance as if
     * it had 18 decimals.
     */
    function _computeScalingFactor(IERC20 token) internal view returns (uint256) {
        // Tokens that don't implement the `decimals` method are not supported.
        uint256 tokenDecimals = ERC20(address(token)).decimals();

        // Tokens with more than 18 decimals are not supported.
        uint256 decimalsDifference = Math.sub(18, tokenDecimals);
        return FixedPoint.ONE * 10**decimalsDifference;
    }

    function _upscale(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {        
        return FixedPoint.mulDown(amount, scalingFactor);
    }

}
