// Implementation of the Verified custody vault
// Manages accounts, participants and vault policies
// (c) Kallol Borah, Verified Network, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./IVault.sol";
import "./math/Math.sol";
import "./math/FixedPoint.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20Detailed.sol";

contract Vault is IVault, Ownable {

    using FixedPoint for uint256;

    //distributor authorized to collect custody fee
    address private distributor;

    // custody fee as percentage of average assets in balance
    uint256 private custodyfee;
    
    //counter for block timestamp nonce
    uint256 private txid;

    //mapping of signer address to creator bytes32 id
    mapping(address => bytes32[]) private signers;

    //mapping signer (its bytes32 ID, eg creator@vault.com) to its address to its token (eg, Google/Apple push notification token)
    mapping(bytes32 => string) private creators;

    //creating vault by mapping its creator (bytes32 ID, eg, creator@vault.com) to participants (bytes32 IDs, eg, participant@email.com) in the vault
    mapping(bytes32 => bytes32[]) private vaults;

    //positional index of cosigners for vault. Mapping of vault creator to participant to its position
    mapping(bytes32 => mapping(bytes32 => uint256)) private cosigners;

    //mapping vault (bytes32 ID, eg creator@vault.com) to participants (bytes32 ID, eg, participant@email.com) to their pin (string)
    mapping(bytes32 => mapping(bytes32 => string)) private pins;

    //mapping vault (bytes32 ID, eg creator@vault.com) to participants (bytes32 ID, eg, participant@email.com) to their key shard (string)
    mapping(bytes32 => mapping(bytes32 => string)) private shards;

    //mapping vault (bytes32 ID, eg creator@vault.com) to number of minimum co-signers required for quorum (uint256)
    mapping(bytes32 => uint256) private quorum;

    //mapping vault (bytes32 ID, eg creator@vault.com) to transactions
    mapping(bytes32 => transaction[]) private transactions;

    //event that helps notify a participant to confirm PIN and shard
    event NewParticipant(bytes32 creator, bytes32 participant);

    //event that helps notify a participant to co-sign a transaction
    event NewTransaction(bytes32 creator, bytes32 participant, uint256 txid);

    //event that helps notify a creator that a transaction has been signed
    event SignTransaction(bytes32 creator, bytes32 participant, uint256 txid);

    //events signalling acceptance or rejection of co-signing requests
    event SignResponse(bytes32 creator, bytes32 participant, bool confirmation);

    //event for fee collection
    event feecollection(address indexed platform, uint256 collection, uint256 timestamp);

    //snapshots for charging custody fee to users
    struct BalanceSnapshot {
        uint256 timestamp;
        uint256 balance;
    }

    //mapping users to tokens to their balance snapshots
    mapping(address => mapping(address => BalanceSnapshot[])) public userSnapshots;

    // mapping token address to issuing fee collected
    mapping(address => uint256) private fees;

    // modifiers
    modifier onlySigner(bytes32 _creator, string memory _id) {
        require(keccak256(abi.encodePacked(creators[_creator])) == keccak256(abi.encodePacked(_id)), 'Not a valid signer');
        _;
    }

    modifier onlyAuthorized(address _caller) {
        require(_caller==distributor, 'Not a valid distributor');
        _;
    }

    constructor() public{
        Ownable.initialize(msg.sender);
        distributor = msg.sender;
    }

    function setDistributor(address _nominee) public onlyAuthorized(msg.sender){
        distributor = _nominee;
    }

    function setCustodyFee(uint256 _fee) public onlyAuthorized(msg.sender){
        custodyfee = _fee;
    }

    /**
        This function creates a vault for the creator
        @param _creator vault creator's username, eg, creator@email.com
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
     */
    function createVault(bytes32 _creator, string calldata _id) external {
        require(vaults[_creator].length==0);
        signers[msg.sender].push(_creator);
        creators[_creator] = _id;
        cosigners[_creator][_creator] = vaults[_creator].length;
        vaults[_creator].push(_creator);
    }


    /**
        This function resets the vault owner's unique id
        @param _creator the vault's creator (eg, creator@email.com)
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _new vault creator's new unique id
     */
    function resetPin(bytes32 _creator, string calldata _id, string calldata _new) onlySigner(_creator, _id) external {
        creators[_creator] = _new;
    }

    /**
        This function returns list of vault creators
     */
    function getVaults() external view returns(bytes32[] memory){
        return signers[msg.sender];
    }

    /**
        This function transfers a vault from the transferor (msg.sender) to transferee
        @param  _creator    vault to transfer
        @param _id          vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param  _transferee address of the new vault operator
     */
    function transferVault(bytes32 _creator, string calldata _id, address _transferee) onlySigner(_creator, _id) external {
        bool found = false;
        for(uint i=0; i<signers[msg.sender].length; i++){
            if(signers[msg.sender][i]==_creator){
                found = true;
                delete signers[msg.sender][i];
            }
        }
        require(found==true, 'Not a valid signer');
        signers[_transferee].push(_creator);
    }

    /**
        This function returns the vault creator's unique ID (eg, Google/APNS messaging token)
        @param _creator the vault creator (eg, creator@email.com)
        @return the vault creator's unique ID
     */
    function getCreator(bytes32 _creator, string calldata _pin) external view returns(string memory){  
        require(vaults[_creator].length>0, "Wallet not created");   
        require(keccak256(abi.encodePacked(pins[_creator][_creator])) == keccak256(abi.encodePacked(_pin)), "Invalid PIN");   
        return creators[_creator];        
    }

    /**
        This function adds a participant to a vault that belongs to its creator
        @param _creator vault creator's username, eg creator@email.com
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _participant vault participant's username, eg participant@email.com
        @param _shard the private key shard of the participant
     */
    function addParticipant(bytes32 _creator, string calldata _id, bytes32 _participant, string calldata _shard) onlySigner(_creator, _id) external {
        //require(vaults[_participant].length>0); //participant does not have a vault yet
        if(keccak256(abi.encodePacked(shards[_creator][_participant]))!=keccak256(abi.encodePacked(_shard)))
            shards[_creator][_participant] = _shard;
        bool present = false;
        for(uint i=0; i<vaults[_creator].length; i++){
            if(vaults[_creator][i]==_participant)
                present = true;
        }
        if(_creator!=_participant && !present){
            cosigners[_creator][_participant] = vaults[_creator].length;
            vaults[_creator].push(_participant); 
            emit NewParticipant(_creator, _participant);
        }
    }

    /**
        This function removes a participant in a vault that belongs to its creator
        @param _creator vault creator's username, eg creator@email.com
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _participant vault participant's username, eg participant@email.com
     */
    function removeParticipant(bytes32 _creator, string calldata _id, bytes32 _participant) onlySigner(_creator, _id) external {
        if(cosigners[_creator][_participant] > 0){
            delete vaults[_creator][cosigners[_creator][_participant]];
            delete shards[_creator][_participant];
            delete pins[_creator][_participant];
        }
    } 

    /**
        This function confirms a participant in the vault when the participant sends its private key shard and PIN
        @param _creator the vault's creator (eg, creator@email.com)
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _participant the vault's participant (eg, participant@email.com)
        @param _pin the PIN number of the participant
        @param _confirmation true or false
     */
    function confirmParticipant(bytes32 _creator, string calldata _id, bytes32 _participant, string calldata _pin, bool _confirmation) onlySigner(_participant, _id) external {
        require(vaults[_creator].length>0);
        if(_confirmation){
            pins[_creator][_participant] = _pin;
            emit SignResponse(_creator, _participant, true);
        }
        else
           emit SignResponse(_creator, _participant, false); 
    }

    /**
        This function defines the number of participants required to co-sign a transaction
        @param _creator the vault's creator (eg, creator@email.com)
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _minParticipants the minimum number of co-signers
     */
    function defineQuorum(bytes32 _creator, string calldata _id, uint256 _minParticipants) onlySigner(_creator, _id) external {
        quorum[_creator] = _minParticipants;
    }

    /**
        This function retrieves the number of participants required to co-sign a transaction
        @param _creator the vault's creator (eg, creator@email.com)
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
     */
    function getQuorum(bytes32 _creator, string calldata _id) onlySigner(_creator, _id) external view returns(uint256){
        return quorum[_creator];
    }

    /**
        This function prompts participants to co-sign a transaction on the vault (ie, the creator's address secured by the quorum of participants)
        @param _creator the vault's creator (eg, creator@email.com)
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
     */
    function promptSignatures(bytes32 _creator, string calldata _id) onlySigner(_creator, _id) external {
        bytes32[] memory cs = new bytes32[](vaults[_creator].length);
        txid = block.timestamp == txid ? txid + 1 : block.timestamp;
        transaction memory currentTx = transaction({
            datetime : txid,
            cosigners : cs
        });
        transactions[_creator].push(currentTx);
        for(uint256 i=0; i<vaults[_creator].length; i++){
            emit NewTransaction(_creator, vaults[_creator][i], txid);
        }
    }

    /**
        This function lets a participant co-sign a transaction
        @param _creator the vault's creator (eg, creator@email.com)
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _participant the vault's participant (eg, participant@email.com)
        @param _tx transaction identifier
        @param _pin the PIN number of the signer (participant)
     */
    function signTransaction(bytes32 _creator, string calldata _id, bytes32 _participant, uint256 _tx, string calldata _pin) onlySigner(_participant, _id) external {
        require(keccak256(abi.encodePacked(pins[_creator][_participant]))==keccak256(abi.encodePacked(_pin)));
        for(uint i=0; i<transactions[_creator].length; i++){
            if(transactions[_creator][i].datetime==_tx){
                for(uint j=0; j<transactions[_creator][i].cosigners.length; j++){
                    if(transactions[_creator][i].cosigners[j]==_participant)
                        break;
                    if(transactions[_creator][i].cosigners[j]==""){
                        transactions[_creator][i].cosigners[j]=_participant;
                        break;
                    }
                }
                emit SignTransaction(_creator, _participant, _tx);
            }
        }
    }

    /**
        This function lets the creator check if quorum has been achieved for a particular transaction
        @param _creator vault creator's username, eg creator@email.com
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _participant vault participant's username, eg participant@email.com
        @param _txid transaction identifier
     */
    function checkQuorum(bytes32 _creator, string calldata _id, bytes32 _participant, uint256 _txid) onlySigner(_creator, _id) external view returns(bool){
        require(keccak256(abi.encodePacked(pins[_creator][_participant]))!=keccak256(abi.encodePacked("")));
        bool _quorum = false;
        for(uint i=0; i<transactions[_creator].length; i++){
            if(transactions[_creator][i].datetime==_txid){
                uint256 signedCount = 0;
                for (uint256 j = 0; j < transactions[_creator][i].cosigners.length; j++) {
                    if (transactions[_creator][i].cosigners[j] != "") {
                        signedCount++;
                    }
                }
                if (signedCount >= quorum[_creator]) {
                    _quorum = true;
                }
            }
        }        
        if(_quorum)
            return true;
        else
            return false;
    }
    
    /**
        This function gets the shard for a participant
        @param _creator vault creator's username, eg creator@email.com
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param _txid transaction identifier 
     */
    function getShards(bytes32 _creator, string calldata _id, uint256 _txid) onlySigner(_creator, _id) external view returns(string[] memory){
        for(uint i=0; i<transactions[_creator].length; i++){
            if(transactions[_creator][i].datetime==_txid){
                if(transactions[_creator][i].cosigners.length >= quorum[_creator]){
                    string[] memory keyShards = new string[](transactions[_creator][i].cosigners.length);
                    for(uint j=0; j<transactions[_creator][i].cosigners.length; j++){
                        keyShards[j] = shards[_creator][transactions[_creator][i].cosigners[j]];                        
                    }
                    //delete transactions[_creator][i];
                    return keyShards;
                }
            }
        }
    }

    /**
        This function takes a snapshot of the token balance for the user
        @param _creator vault creator's username, eg creator@email.com
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param token token address
     */
    function snapshotBalance(bytes32 _creator, string calldata _id, address token) onlySigner(_creator, _id) external {
        //uint256 currentBalance = 1;
        uint256 currentBalance = IERC20(token).balanceOf(msg.sender);
        userSnapshots[msg.sender][token].push(BalanceSnapshot(block.timestamp, currentBalance));
    }
    //event totalCount(uint256 total, uint256 count);
    //event avgBalance(uint256 fees, uint256 totalCount, uint256 custodyfee);
    /**
        This function calculates the average balance for a user and a token
        @param _creator vault creator's username, eg creator@email.com
        @param _id vault creator's unique id using which it can be reached, eg, its Google/APNS token
        @param token token address
        @param fromTime time to start calculating token balance from
        @param toTime time to end calculating token balance 
     */
    function calculateAverageBalance(bytes32 _creator, string calldata _id, address token, uint256 fromTime, uint256 toTime) onlySigner(_creator, _id) external {
        BalanceSnapshot[] storage snapshots = userSnapshots[msg.sender][token];
        uint256 total = 0;
        uint256 count = 0;

        for (uint i = 0; i < snapshots.length; i++) {
            if (snapshots[i].timestamp >= fromTime && snapshots[i].timestamp <= toTime) {
                total += snapshots[i].balance;
                count++;                
            }
        }
        //emit totalCount(total, count);
        if (count != 0){      
            IERC20(token).transferFrom(msg.sender, address(this), FixedPoint.mulDown(custodyfee, FixedPoint.divDown(total, count)));      
            //emit avgBalance(fees[token], custodyfee, FixedPoint.mulDown(custodyfee, FixedPoint.divDown(total, count)));
            fees[token] = Math.add(fees[token], FixedPoint.mulDown(custodyfee, FixedPoint.divDown(total, count)));
            //emit avgBalance(fees[token], custodyfee, FixedPoint.mulDown(custodyfee, FixedPoint.divDown(total, count)));
        } 
    }

    /**
        This function gets the shard for a participant
        @param _token vault creator's username, eg creator@email.com
     */
    function collectCustodyFee(address _token) external returns(uint256){
        require(msg.sender==distributor, "Unauthorized fee collection");
        uint256 feeCollected = fees[_token];        
        fees[_token] = Math.sub(fees[_token], feeCollected);
        IERC20(_token).transfer(distributor, feeCollected);
        emit feecollection(address(this), feeCollected, block.timestamp);
        return feeCollected;
    }

    /**
     * @dev Returns a scaling factor that, when multiplied to a token amount for `token`, normalizes its balance as if
     * it had 18 decimals.
     */
    function _computeScalingFactor(IERC20 token) internal view returns (uint256) {
        // Tokens that don't implement the `decimals` method are not supported.
        uint256 tokenDecimals = ERC20Detailed(address(token)).decimals();

        // Tokens with more than 18 decimals are not supported.
        uint256 decimalsDifference = Math.sub(18, tokenDecimals);
        return FixedPoint.one() * 10**decimalsDifference;
    }

    /**
     * @dev Reverses the `scalingFactor` applied to `amount`, resulting in a smaller or equal value depending on
     * whether it needed scaling or not. The result is rounded down.
     */
    function _downscaleDown(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        return FixedPoint.divDown(amount, scalingFactor);
    }

    /**
     * @dev Applies `scalingFactor` to `amount`, resulting in a larger or equal value depending on whether it needed
     * scaling or not.
     */
    function _upscale(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        // Upscale rounding wouldn't necessarily always go in the same direction: in a swap for example the balance of
        // token in should be rounded up, and that of token out rounded down. This is the only place where we round in
        // the same direction for all amounts, as the impact of this rounding is expected to be minimal (and there's no
        // rounding error unless `_scalingFactor()` is overriden).
        return FixedPoint.mulDown(amount, scalingFactor);
    }

}

