// Factory for Verified cash and bond tokens
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;

import "./interfaces/IFactory.sol";
import "./interfaces/ISecurity.sol";
import "abdk-libraries-solidity/ABDKMathQuad.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/upgrades/contracts/upgradeability/ProxyFactory.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20.sol";

contract SecuritiesFactory is IFactory, ProxyFactory, Initializable, Ownable{

    using ABDKMathQuad for uint256;
    using ABDKMathQuad for int256;
    using ABDKMathQuad for bytes16;

    //events
    event securitiesAdded(address indexed security, address issuer, bytes32 isin, bytes32 currency);
    event IssuerCreated(address indexed issuer, bytes32 tokenName, bytes32 tokenType);

    struct via{
        bytes32 tokenType;
        bytes32 name;        
    }

    struct securities{
        bytes32 currency;
        bytes32 company;
        bytes32 isin;
        address token;
        bytes16 amount;
    }

    //addresses of all Via proxies
    mapping(address => via) public token;

    //mapping token type to token name to issuer address
    mapping(bytes32 => mapping(bytes32 => address)) issuers;

    address[] public tokens;
    address[] public issues;

    //mapping security to holder to securities struct
    mapping(address => mapping(address => securities)) private securitiesHoldings;

    //mapping security to issuer
    mapping(address => address) private holders;
    mapping(address => address) private securityTokens;

    address private securityAddress;

    address private bridge;

    modifier onlySigner(bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s){
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge);
        _;
    }

    function initialize(address _security, address _bridge) public initializer{
        Ownable.initialize(msg.sender);
        securityAddress = _security;
        bridge = _bridge;
    }

    function setSigner(address _signer) onlyOwner external{
        bridge = _signer;
    }

    function getTokenCount() external view returns(uint tokenCount) {
        return tokens.length;
    }

    function getToken(uint256 n) external view returns(address){
        return tokens[n];
    }

    function getName(address viaAddress) external view returns(bytes32) {
        return token[viaAddress].name;
    }

    function getType(address viaAddress) external view returns(bytes32) {
        return token[viaAddress].tokenType;
    }

    function getNameAndType(address viaAddress) external view returns(bytes32, bytes32){
        return (token[viaAddress].name, token[viaAddress].tokenType);
    }

    function getTokenByNameType(bytes32 tokenName, bytes32 tokenType) external view returns(address){
        for(uint256 q=0; q<tokens.length; q++){
            if(token[tokens[q]].name == tokenName && token[tokens[q]].tokenType == tokenType)
                return tokens[q];
        }
    }

    function getIssuer(bytes32 tokenType, bytes32 tokenName) external view returns(address){
        return issuers[tokenType][tokenName];
    }

    function getIssues() external view returns(address[] memory){
        return issues;
    }

    function createIssuer(address _target, bytes32 tokenName, bytes32 tokenType, bytes32 tokenCurrency, address feeRate) onlyOwner external{
        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,bytes32,address,address,address)", tokenName, tokenCurrency, owner(), feeRate, bridge);

        // Deploy proxy
        address _issuer = deployMinimal(_target, _payload);
        emit IssuerCreated(_issuer, tokenName, tokenType);

        if(tokenType == "Cash"){
            token[_issuer] = via("ViaCash", tokenCurrency);
            tokens.push(_issuer);
            issuers["ViaCash"][tokenCurrency] = _issuer;
        }
        else if(tokenType == "Bond"){
            token[_issuer] = via("ViaBond", tokenCurrency);
            tokens.push(_issuer);
            issuers["ViaBond"][tokenCurrency] = _issuer;
        }

    }

    function issueSecurity(address security, bytes32 company, bytes32 isin, bytes32 currency, address issuer, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external {  
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge || issuer==msg.sender, "Unauthorized issuer of security.");

        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,bytes32,address,address)", company, isin, address(this), bridge);
        // Deploy proxy
        address _securityToken = deployMinimal(securityAddress, _payload);

        securitiesHoldings[_securityToken][issuer].isin= isin;
        securitiesHoldings[_securityToken][issuer].company = company;
        securitiesHoldings[_securityToken][issuer].currency= currency;
        securitiesHoldings[_securityToken][issuer].token = _securityToken;
        holders[_securityToken] = issuer;   
        securityTokens[_securityToken] = security;
        issues.push(_securityToken);

        emit securitiesAdded(_securityToken, issuer, isin, currency);
    }

    function getHolder(address _securityToken) external view returns(address){
        return holders[_securityToken];
    }

    function getSecurity(address _securityToken) external view returns(address){
        return securityTokens[_securityToken];
    }

    function getSecurityToken(address _securityToken, address _issuer) external view returns(address, bytes32, bytes32, bytes32){
        return (securitiesHoldings[_securityToken][_issuer].token, 
                securitiesHoldings[_securityToken][_issuer].isin,
                securitiesHoldings[_securityToken][_issuer].company,
                securitiesHoldings[_securityToken][_issuer].currency);
    }
    
    function addBalance(address security, address transferor, address transferee, uint256 amount, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== bridge || holders[security]==msg.sender, "Unauthorized attempt to add security balance.");

        if(transferor!=address(0x0)){
            securitiesHoldings[security][transferee].amount= ABDKMathQuad.add(securitiesHoldings[security][transferee].amount, ABDKMathQuad.fromUInt(amount));
            securitiesHoldings[security][transferor].amount= ABDKMathQuad.sub(securitiesHoldings[security][transferor].amount, ABDKMathQuad.fromUInt(amount));
            ISecurity(securitiesHoldings[security][holders[security]].token).transferToken(transferor, transferee, amount);
        } else {
            securitiesHoldings[security][transferee].amount= ABDKMathQuad.fromUInt(amount);
            ISecurity(securitiesHoldings[security][holders[security]].token).mintToken(transferee, amount);
        }
    }

    function checkProduct(address issue) external view returns(bool){
        if(securityTokens[issue]!=address(0x0) || holders[issue]!=address(0x0))
            return true;
        else
            return false;
    }
    
}
