// Verified Liquidity Manager 
// (c) Kallol Borah, 2021

//"SPDX-License-Identifier: UNLICENSED"

pragma solidity 0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "./interfaces/IMarketMaker.sol";
import "./interfaces/ILiquidity.sol";
import "./interfaces/IVitta.sol";

import "./math/FixedPoint.sol";
import "./math/Math.sol";

contract Liquidity is Ownable, Pausable {

    using SafeMath for uint256;
    using FixedPoint for uint256;

    ILiquidity.platform[] private platforms;
    mapping(address => uint256) private platformIndex;

    ILiquidity.token[] private tokens;
    mapping(address => uint256) private tokenIndex;
    
    ILiquidity.provider[] private providers;
    address[] private LPs;

    uint256 private investmentCap=0;
    uint256 private timeLimit=0;    
    uint256 private totalIssued=0;

    address private vitta;

    //for share of liquidity staked with Verified
    address private treasury;
    uint256 private liquidityShare;

    struct investment{
        uint256 tokenAmount;
        address tokenAddress;
    }    

    struct performance{
        uint256 liquidityProvided;
        uint256 commissionsEarned;
    }

    // mapping investor to invested tokens
    mapping(address => mapping(address => investment)) private investors;
    // mapping investor to liquidity tokens issued and staked
    mapping(address => uint256) private stakes;    
    // mapping platform to asset managers on it
    mapping(address => address[]) private managers; 
    // mapping platform to asset manager's position in it
    mapping(address => mapping(address => uint256)) private managerIndex;
    // mapping platform to address of token distributed by manager to asset manager index to performance of liquidity provided
    mapping(address => mapping(address => mapping(uint256 => performance))) private managerRoi;
    // mapping platform to performance
    mapping(address => performance) private platformRoi;
    // exchange rate of support token to vitta
    mapping(address => uint256) private exchangeRate;
    // indexing liquidity providers
    mapping(address => uint256) private LPIndex;

    // oracle
    address private oracle;

    modifier onlyOracle(address sender){
        require(oracle==sender, "Unauthorized oracle");
        _;
    }

    modifier onlyTreasurer(address sender){
        require(treasury==sender, "Unauthorized treasurer");
        _;
    }

    //mutex
    mapping(bytes32 => bool) private lock;

    event SupportToken(address indexed token, string tokenName);
    event RegisterPlatform(address indexed platform, bytes32 platformName);
    event PlatformManagers(address indexed platform, address manager);
    event RequestRate(address indexed token, uint256 amount);
    event RequestIssue(address indexed investor, address token, uint256 tokenAmount, uint256 timestamp);
    event IssuedVitta(address indexed investor, address token, uint256 tokenAmount, uint256 LPToIssue);
    event PlatformLiquidity(address indexed platform, address manager, uint256 LPToken, address token, uint256 tokenAmount);
    event ManagerReturns(address indexed platform, address indexed manager, address token, uint256 distribution);
    event PlatformReturns(address indexed platform, address vitta, uint256 distribution);
    event InvestorReturns(address indexed platform, address indexed investor, address vitta, uint256 prorataStake, uint256 timestamp);
      
    /**
        Constructor for the Verified Liquidity manager
        @param  _cap    investment limit for each investor
        @param  _limit  time limit for issue close
        @param  _vitta  address of Verified liquidity token
     */
    constructor(uint256 _cap, uint256 _limit, uint256 _share, address _vitta){        
        investmentCap = _cap;
        timeLimit = _limit;
        totalIssued = 0;
        liquidityShare = _share;
        vitta = _vitta; 
        treasury = owner();       
    }

    /**
        Updates investment and time limits for Verified Liquidity token after initial issue with constructor defined parameters
        @param  _cap    investment limit for each investor
        @param  _limit  time limit for issue close 
     */
    function createSupply(uint256 _cap, uint256 _limit) external onlyOwner {
        investmentCap = _cap;
        timeLimit = _limit;
    }

    /**
        Specifies list of supported tokens that can be invested in the Verified Liquidity token
        @param  _token  address of token supported
        @param  _name   name of token supported
     */
    function supportTokens(address _token, string memory _name) external onlyOwner {
        if(tokenIndex[_token]==0){
            ILiquidity.token memory t = ILiquidity.token({
                tokenName: _name,
                tokenAddress: _token
            });
            tokens.push(t);
            tokenIndex[_token] = tokens.length;
            emit SupportToken(_token, _name);
        }
    }

    /**
        Checks if a specified token is supported for investing in the Verified Liquidity token
        @param  _token  token that is supported for investment
     */
    function checkSupportForToken(address _token) external view returns(bool){
        if(tokenIndex[_token]>0)
            return true;
        else
            return false;
    }

    /**
        Returns list of supported tokens
     */
    function getSupportedTokens() external view returns(ILiquidity.token[] memory){
        return tokens;
    }

    /**
        Removes a supported token
     */
    function removeToken(address _token) external onlyOwner {
        bool found = false;
        if(tokenIndex[_token]!=0){
            uint length = tokens.length;
            for(uint i=0; i<length; i++){
                if(tokens[i].tokenAddress==_token){
                    if(i==tokens.length-1){
                       delete tokens[i];
                       tokenIndex[_token] = 0;
                    }
                    else{
                       tokens[i]=tokens[i+1];
                       tokenIndex[tokens[i].tokenAddress] = tokenIndex[tokens[i].tokenAddress] - 1;
                    }
                    found = true;
                }
                else if(found){
                    if(i==tokens.length-1){
                       delete tokens[i];
                       tokenIndex[_token] = 0;
                    }
                    else{
                       tokens[i]=tokens[i+1];
                       tokenIndex[tokens[i].tokenAddress] = tokenIndex[tokens[i].tokenAddress] - 1;
                    }
                }
            }
        }
    }

    /**
        Registers a liquidity platform (eg, DEX) where Verified Liquidity tokens can be used to underwrite investments
        @param  _liquidityPlatform  address of the market making platform
        @param  _platformName       name of market making platform
     */
    function registerPlatform(address _liquidityPlatform, bytes32 _platformName) external onlyOwner {
        if(platformIndex[_liquidityPlatform]==0){
            ILiquidity.platform memory p = ILiquidity.platform({
                platformName: _platformName,
                platformAddress: _liquidityPlatform
            });
            platforms.push(p);
            platformIndex[_liquidityPlatform] = platforms.length;
            emit RegisterPlatform(_liquidityPlatform, _platformName);
        }
    }
    
    /**
        Gets list of registered platforms
     */
    function getPlatforms() external view returns(ILiquidity.platform[] memory){
        return platforms;
    }

    /**
        Adds asset manager for managing liquidity
        @param  _platform   address of market making platform (eg, Balancer)
        @param  _manager    address of asset manager
     */
    function addManager(address _platform, address _manager) external onlyOwner {
        if(managerIndex[_platform][_manager]==0){
            managers[_platform].push(_manager);
            managerIndex[_platform][_manager] = managers[_platform].length;
            emit PlatformManagers(_platform, _manager);
        }
    } 

    /**
        Removes asset manager for managing liquidity on platform
        @param  _platform   address of market making platform
        @param  _manager    address of asset manager
     */
    function removeManager(address _platform, address _manager) external onlyOwner {
        bool found = false;
        if(managerIndex[_platform][_manager]!=0){
            uint length = managers[_platform].length;
            for(uint i=0; i<length; i++){
                if(managers[_platform][i]==_manager){
                    if(i==managers[_platform].length-1){
                       delete managers[_platform][i];
                       managerIndex[_platform][_manager] = 0;
                    }
                    else{
                       managers[_platform][i]=managers[_platform][i+1];
                       managerIndex[_platform][managers[_platform][i]] = managerIndex[_platform][managers[_platform][i]] - 1;
                    }
                    found = true;
                }
                else if(found){
                    if(i==managers[_platform].length-1){
                       delete managers[_platform][i];
                       managerIndex[_platform][_manager] = 0;
                    }
                    else{
                       managers[_platform][i]=managers[_platform][i+1];
                       managerIndex[_platform][managers[_platform][i]] = managerIndex[_platform][managers[_platform][i]] - 1;
                    }
                }
            }
        }
    }

    /**
        Gets asset managers for managing liquidity
        @param  _platform   address of market making platform
     */
    function getManagers(address _platform) external view onlyOwner returns(address[] memory){
        return managers[_platform];
    }
    
    function saveInvestor(address investor) private {
        bool found = false;
        uint length = tokens.length;
        for(uint256 i=0; i<length; i++){
            if(investors[investor][tokens[i].tokenAddress].tokenAmount > 0)
                found = true;
        }
        if(!found){
            LPs.push(investor);
        }
    }

    /**
        Receiver of ether invested in the Verified Liquidity token
     */
    receive() external payable{
        if(timeLimit!=0)
            require(timeLimit > block.timestamp, "Subscription window breached");
        //ether paid in
        require(msg.value !=0, "Zero eth sent");
        //contract must not be paused
        require(paused() == false, "Contract in paused state");
        //block reentrants
        require(!lock["etherreceipt"], "Locked to avoid reentrancy");
        lock["etherreceipt"] = true;
        //issue liquidity token        
        if(investors[msg.sender][address(0x0)].tokenAmount==0){
            ILiquidity.provider memory lp = ILiquidity.provider({
                assetInvested: string("ether"),
                investorAddress: msg.sender
            });
            providers.push(lp);
        }
        if(LPIndex[msg.sender]==0){
            LPs.push(msg.sender);
            LPIndex[msg.sender] = LPs.length;
        }
        investors[msg.sender][address(0x0)].tokenAmount = SafeMath.add(investors[msg.sender][address(0x0)].tokenAmount, msg.value);
        emit RequestIssue(msg.sender, address(0x0), msg.value, block.timestamp);
        if(exchangeRate[address(0x0)]!=0){
            issueVitta(msg.sender, address(0x0), msg.value, FixedPoint.mulDown(msg.value, exchangeRate[address(0x0)]), owner());
            emit RequestRate(address(0x0), exchangeRate[address(0x0)]);
        }
        lock["etherreceipt"] = false;
    }

    /**
        Used by external apps (eg, exchange, wallet) to buy Verified Liquidity token 
        @param  _token  address of token used by investor to buy the VITTA
        @param  _amount amount of token that is transferred from investor to this VITTA issuing contract's account for the investor
     */
    function buy(address _token, uint256 _amount) external {
        if(timeLimit!=0)
            require(timeLimit > block.timestamp, "Subscription window breached");
        require(paused() == false, "Contract in paused state");
        bool found = false;
        if(tokenIndex[_token]>0)
            found = true;
        if(found){
            require(!lock["buy"], "Locked to prevent reentrancy");
            lock["buy"] = true;
            if(IERC20(_token).transferFrom(msg.sender, address(this), _amount)){
                emit RequestIssue(msg.sender, _token, _amount, block.timestamp);
                if(LPIndex[msg.sender]==0){
                    LPs.push(msg.sender);
                    LPIndex[msg.sender] = LPs.length;
                }
                if(investors[msg.sender][_token].tokenAmount==0){
                    ILiquidity.provider memory lp = ILiquidity.provider({
                        assetInvested: ERC20(_token).name(),
                        investorAddress: msg.sender
                    });
                    providers.push(lp);
                }
                investors[msg.sender][_token].tokenAmount = SafeMath.add(investors[msg.sender][_token].tokenAmount, _amount);
                investors[msg.sender][_token].tokenAddress = _token;                
                if(exchangeRate[_token]!=0){
                    issueVitta(msg.sender, _token, _amount, FixedPoint.mulDown(_upscale(_amount, _computeScalingFactor(IERC20(_token))), exchangeRate[_token]), owner());
                    emit RequestRate(_token, exchangeRate[_token]);
                }
            }
            lock["buy"] = false;
        }
    }

    /**
        Fetches investors in VITTA
     */
    function getInvestors() external view onlyOwner returns(ILiquidity.provider[] memory){
        return providers;
    }

    /**
        Fetches investment detail for specific investor in VITTA
        @param  _investor   address of investor
        @param  _token      address of token invested by investor
     */
    function getInvestment(address _investor, address _token) external view onlyOwner returns(uint256){
        return (investors[_investor][_token].tokenAmount);
    }
    
    /**
        Used by admin to issue VITTA to investors or refund paid in tokens to investors if investment cap is breached
        @param  _investor       address of investor
        @param  _token          address of token invested in VITTA
        @param  _tokenAmount    amount of token invested in VITTA
        @param  _LPToIssue      amount of VITTA to issue to investor 
     */
    function issue(address _investor, address _token, uint256 _tokenAmount, uint256 _LPToIssue) external onlyOwner {
        issueVitta(_investor, _token, _tokenAmount, _LPToIssue, msg.sender);
    }
    
    function issueVitta(address _investor, address _token, uint256 _tokenAmount, uint256 _LPToIssue, address _issuer) private {
        require(!lock["issue"], "Locked to prevent renentrancy");
        lock["issue"] = true;
        investors[_investor][_token].tokenAmount = SafeMath.sub(investors[_investor][_token].tokenAmount, _tokenAmount);
        //If investment is outside limits, refund tokens invested back to investor
        if((_LPToIssue > investmentCap) || (_LPToIssue > SafeMath.sub(IERC20(vitta).totalSupply(), totalIssued))){
            if(investors[_investor][_token].tokenAddress!=address(0x0))
                IERC20(investors[_investor][_token].tokenAddress).transfer(_investor, _tokenAmount);
            else
                payable(_investor).transfer(_tokenAmount);
        }
        //else issue VITTA to investor and transfer managed liquidity to deployer that allocates it to issuer
        else{
            IERC20(vitta).transferFrom(_issuer, _investor, _LPToIssue);  
            totalIssued = SafeMath.add(totalIssued, _LPToIssue);    
            stakes[_investor] = SafeMath.add(stakes[_investor], _LPToIssue);
            emit IssuedVitta(_investor, investors[_investor][_token].tokenAddress, _tokenAmount, _LPToIssue);
            if(investors[_investor][_token].tokenAddress!=address(0x0))                                                        
                IERC20(investors[_investor][_token].tokenAddress).transfer(treasury, _downscaleDown(FixedPoint.mulDown(_upscale(_tokenAmount, _computeScalingFactor(IERC20(_token))), liquidityShare), _computeScalingFactor(IERC20(_token))));
            else
                payable(treasury).transfer(FixedPoint.mulDown(_tokenAmount, liquidityShare));
        }        
        lock["issue"] = false;
    }
    
    /**
        Provides staked VITTA to asset managers based on prior liquidity consumed and commission earned 
        @param  _platform   address of platform on which liquidity has to be provided
        @param  _manager    address of asset manager to whom liquidity has to be provided
        @param  _liquidity  amount of VITTA tokens to allot to an asset manager
        @param  _token      address of token allotted to asset manager
        @param  _tokenAmount    amount of token allotted to asset manager
     */    
    function provideLiquidity(address _platform, address _manager, uint256 _liquidity, address _token, uint256 _tokenAmount) external onlyOwner {
        require(!lock["liquidity"], "Locked to prevent reentrancy");
        lock["liquidity"] = true;
        require(paused() == false, "Contract in paused state");
        require(managerIndex[_platform][_manager]!=0, "Manager does not exist");
        //provide liquidity in native tokens invested by LPs
        if(_token!=address(0x0)) //not ether
            IERC20(_token).transfer(_platform, _tokenAmount);
        else //if ether
            require(payable(_platform).send(_tokenAmount), "Transfer failed");    
        IMarketMaker(_platform).stake(_tokenAmount, _token, _manager);
        emit PlatformLiquidity(_platform, _manager, _liquidity, _token, _tokenAmount);
        platformRoi[_platform].liquidityProvided = SafeMath.add(platformRoi[_platform].liquidityProvided, _liquidity);
        uint256 index = managerIndex[_platform][_manager];
        managerRoi[_platform][_token][index].liquidityProvided = SafeMath.add(managerRoi[_platform][_token][index].liquidityProvided, _tokenAmount);
        lock["liquidity"] = false;
    }

    /**
        Pay out of income by Market maker contracts to VITTA Liquidity providers
        @param  _distribution   amount of VITTA to mint and distribute pro rata to liquidity providers
        @param  _token          address of token in which distribution is made by market maker    
        @param  _manager        address of asset manager earning the paid out return on investment 
     */
    function distribute(uint256 _distribution, address _token, address _manager) external {
        require(platformIndex[msg.sender]>0, "Liquidity pool does not exist");
        require(tokenIndex[_token]>0, "Unsupported token");
        require(paused() == false, "Contract in paused state");
        require(!lock["distribute"], "Locked to prevent reentrancy");
        lock["distribute"] = true;
        uint256 index = managerIndex[msg.sender][_manager];
        if(totalIssued!=0 && index!=0){            
            managerRoi[msg.sender][_token][index].commissionsEarned = SafeMath.add(managerRoi[msg.sender][_token][index].commissionsEarned, _distribution);
            emit ManagerReturns(msg.sender, _manager, _token, _distribution);
        }
        lock["distribute"] = false;
    }

    /**
        Fetches performance of market platform platform
        @param  _platform   address of market making platform
     */
    function getPlatformPerformance(address _platform) external view onlyOwner returns(uint256 platformLiquidityProvided,
                                                                                        uint256 platformCommissionsEarned){                                                                                        
        return (platformRoi[_platform].liquidityProvided, platformRoi[_platform].commissionsEarned);
    }

    /**
        Fetches performance of asset managers
        @param  _platform   address of platform
        @param  _token      address of token
        @param  _manager    address of asset manager on platform
     */
    function getManagerPerformance(address _platform, address _token, address _manager) external view onlyOwner returns(uint256 managerLiquidityProvided,
                                                                                    uint256 managerCommissionsEarned){
        uint256 index = managerIndex[_platform][_manager];                                                                                
        return (managerRoi[_platform][_token][index].liquidityProvided, managerRoi[_platform][_token][index].commissionsEarned);
    }
    
    /**
        Pay out income to VITTA Liquidity providers
        @param  _distribution   amount of VITTA to mint and distribute pro rata to liquidity providers   
        @param  _platform       address of platform on which commission is earned based on which distribution is made
     */
    function payOut(uint256 _distribution, address _platform) onlyOwner external {
        require(!lock["payout"], "Locked to prevent renentrancy");
        lock["payout"] = true;
        IVitta(vitta).mint(address(this), _distribution);
        platformRoi[_platform].commissionsEarned = SafeMath.add(platformRoi[_platform].commissionsEarned, _distribution);
        emit PlatformReturns(_platform, vitta, _distribution);
        uint length = LPs.length;
        uint issued = totalIssued;
        for(uint i=0; i<length; i++){
            uint256 prorataStake = FixedPoint.mulDown(FixedPoint.divDown(stakes[LPs[i]], issued), _distribution);
            IERC20(vitta).transfer(LPs[i], prorataStake);   
            emit InvestorReturns(_platform, LPs[i], vitta, prorataStake, block.timestamp);
            totalIssued = SafeMath.add(totalIssued, prorataStake);    
            stakes[LPs[i]] = SafeMath.add(stakes[LPs[i]], prorataStake);             
        }
        lock["payout"] = false;
    }

    function balance(address investor) external view returns(uint256 investorBalance){
        return IERC20(vitta).balanceOf(investor);
    }

    /**
        Adjust stakes for every transfer of Vitta
        @param  sender      transferor of Vitta
        @param  recipient   transferee of vitta
        @param  amount      amount of vitta transferred
     */
    function transfer(address sender, address recipient, uint256 amount) external {
        require(msg.sender==vitta, "Unauthorized sender");
        require(stakes[sender]>=amount, "Insufficient balance");
        saveInvestor(recipient);
        stakes[recipient] = SafeMath.add(stakes[recipient], amount);
        stakes[sender] = SafeMath.sub(stakes[sender], amount);
    }

    /**
        Pauses this contract
     */
    function pause() public onlyOwner {
        _pause();
    }
    
    /**
        Unpauses this contract
     */
    function unpause() public onlyOwner {
        _unpause();
    }

    function setOracle(address _oracle) public onlyOwner {
        oracle = _oracle;
    }
    
    function setExchangeRate(address token, uint256 rate) public onlyOracle(msg.sender) {
        if(tokenIndex[token]>0){
            exchangeRate[token] = rate;
        }
    }

    function setTreasurer(address _treasurer) public onlyTreasurer(msg.sender){
        treasury = _treasurer;
    }

    function setLiquidityShare(uint256 _share) public onlyTreasurer(msg.sender){
        liquidityShare = _share;
    }

    /**
     * @dev Returns a scaling factor that, when multiplied to a token amount for `token`, normalizes its balance as if
     * it had 18 decimals.
     */
    function _computeScalingFactor(IERC20 token) internal view returns (uint256) {
        // Tokens that don't implement the `decimals` method are not supported.
        uint256 tokenDecimals = ERC20(address(token)).decimals();
        
        // Tokens with more than 18 decimals are not supported.
        uint256 decimalsDifference = Math.sub(18, tokenDecimals);
        return FixedPoint.ONE * 10**decimalsDifference;
    }

    /**
     * @dev Reverses the `scalingFactor` applied to `amount`, resulting in a smaller or equal value depending on
     * whether it needed scaling or not. The result is rounded down.
     */
    function _downscaleDown(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        return FixedPoint.divDown(amount, scalingFactor);
    }

    /**
     * @dev Applies `scalingFactor` to `amount`, resulting in a larger or equal value depending on whether it needed
     * scaling or not.
     */
    function _upscale(uint256 amount, uint256 scalingFactor) internal pure returns (uint256) {
        return FixedPoint.mulDown(amount, scalingFactor);
    }
    
}

