pragma solidity 0.5.7;

import "./interfaces/ViaFactory.sol";
import "./interfaces/ViaToken.sol";
import "./interfaces/VerifiedClient.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/upgrades/contracts/upgradeability/ProxyFactory.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";

contract Factory is ViaFactory, ProxyFactory, Initializable, Ownable {

    //data structure for token proxies
    struct via{
        bytes32 tokenType;
        bytes32 name;        
    }

    //addresses of all Via proxies
    mapping(address => via) public token;

    //Via oracle url address
    address ViaOracle;
    string ViaOracleUrl;

    //Payout url
    string FiatPayoutUrl;

    //Crypto data url
    mapping(bytes32 => mapping(bytes32 => string)) CryptoDataUrl;

    //Oracle address
    address[] oracles;

    //Verified accounts client
    address client;    

    //token index and position in index
    address[] public tokens;

    //mapping token name (currency) to index position 
    mapping(bytes32 => uint256) private tokenIndex;

    //list of token type to token currency to product issuers
    mapping(bytes32 => mapping(bytes32 => address)) issuers;

    //mapping bond token to term in seconds
    mapping(address => uint256) private term;

    //storing supporting ERC20 tokens
    bytes32[] public currencies;
    mapping(bytes32 => uint256) private currencyIndex;
    mapping(bytes32 => address) private currencyAddress;

    event CashIssuerCreated(address indexed issuer, bytes32 tokenName, bytes32 tokenType);
    event BondIssuerCreated(address indexed issuer, bytes32 tokenName, bytes32 tokenType);
    event TokenCreated(address indexed token, bytes32 tokenName, bytes32 tokenType);

    modifier onlyManager(address sender){
        bytes32 role = "";
        if(client!=address(0x0))
            (role, , ) = VerifiedClient(client).getRole(sender);
        require(role=="DP" || role=="Admin" || sender==owner());
        _;
    }

    function initialize() public initializer{
        Ownable.initialize(msg.sender);
    }

    function getTokenCount() external view returns(uint tokenCount) {
        return tokens.length;
    }

    function getToken(uint256 n) external view returns(address){
        return tokens[n];
    }

    function getName(address viaAddress) external view returns(bytes32) {
        return token[viaAddress].name;
    }

    function getType(address viaAddress) external view returns(bytes32) {
        return token[viaAddress].tokenType;
    }

    function getNameAndType(address viaAddress) external view returns(bytes32, bytes32){
        return (token[viaAddress].name, token[viaAddress].tokenType);
    }

    function getTokenByNameType(bytes32 tokenName, bytes32 tokenType) external view returns(address){
        if(token[tokens[tokenIndex[tokenName]]].name == tokenName && token[tokens[tokenIndex[tokenName]]].tokenType == tokenType)
                return tokens[tokenIndex[tokenName]];
    }

    //retrieve token product issuer address for given token name and type
    function getIssuer(bytes32 tokenType, bytes32 tokenName) external view returns(address){
        return issuers[tokenType][tokenName];
    }

    //retrieves address and type of token for name specified
    function getAddressAndType(bytes32 tokenName) external view returns (address, bytes32){
        bool found = false;
        if(token[tokens[tokenIndex[tokenName]]].name==tokenName)
            found == true;
        if(found)
            return (tokens[tokenIndex[tokenName]], token[tokens[tokenIndex[tokenName]]].tokenType);
        else
            return (address(0x0), "");
    }

    function getViaOracleUrl() external view returns(string memory){
        require(msg.sender == ViaOracle);
        return ViaOracleUrl;
    }

    function getFiatPayoutUrl() external view returns(string memory){
        require(msg.sender == ViaOracle);
        return FiatPayoutUrl;
    }

    function getCryptoDataUrl(bytes32 fromCurrency, bytes32 toCurrency) external view returns(string memory){
        require(msg.sender == ViaOracle);
        return CryptoDataUrl[fromCurrency][toCurrency];
    }

    //get Verified client address for AML check
    function getClient() external view returns(address){
        require(token[msg.sender].tokenType == "ViaCash" || token[msg.sender].tokenType == "ViaBond", 'Get client : FORBIDDEN');
        return client;
    }

    function getOracles() external view returns(address[] memory){
        require(msg.sender == ViaOracle);
        return oracles;
    }

    function getBondTerm(address _bondToken) external view returns(uint256){
        return term[_bondToken];
    }

    //token issuer factory 
    function createIssuer(address _target, bytes32 tokenName, bytes32 tokenType, bytes32 tokenCurrency, address _oracle, address _token, address _fee) onlyOwner external{
        ViaOracle = _oracle;
        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,bytes32,address,address,address,address)",tokenName,tokenCurrency,owner(),_oracle,_token,_fee);

        // Deploy proxy
        address _issuer = deployMinimal(_target, _payload);
        if(tokenType=="Cash")
            emit CashIssuerCreated(_issuer, tokenName, tokenType);
        else if(tokenType == "Bond")
            emit BondIssuerCreated(_issuer, tokenName, tokenType);

        if(tokenType == "Cash"){
            if(issuers["ViaCash"][tokenCurrency]!=address(0x0)){
                token[_issuer] = via("ViaCash", tokenName);
                tokens[tokenIndex[tokenName]] = _issuer;
                issuers["ViaCash"][tokenCurrency] = _issuer;
            }
            else{
                token[_issuer] = via("ViaCash", tokenName);
                tokenIndex[tokenName]=tokens.length;
                tokens.push(_issuer);                
                issuers["ViaCash"][tokenCurrency] = _issuer;
            }
        }
        else if(tokenType == "Bond"){
            if(issuers["ViaBond"][tokenCurrency]!=address(0x0)){
                token[_issuer] = via("ViaBond", tokenName);
                tokens[tokenIndex[tokenName]] = _issuer;
                issuers["ViaBond"][tokenCurrency] = _issuer;
            }
            else{
                token[_issuer] = via("ViaBond", tokenName);
                tokenIndex[tokenName]=tokens.length;
                tokens.push(_issuer);                
                issuers["ViaBond"][tokenCurrency] = _issuer;
            }
        }
    }
    
    //token factory
    function createToken(address _target, address issuer, bytes32 tokenName, bytes32 tokenSymbol) external returns(address){
        require(token[msg.sender].tokenType == "ViaBond" || msg.sender==owner());

        bytes memory _payload = abi.encodeWithSignature("initialize(bytes32,address,address,bytes32)", tokenName, issuer, msg.sender, tokenSymbol);

        // Deploy proxy
        address _token = deployMinimal(_target, _payload);
        emit TokenCreated(_token, tokenSymbol, "ViaBond");
        
        via storage t = token[_token];
        t.tokenType = "ViaBondToken";
        tokens.push(_token);
        
        return _token;
    }

    function setViaOracleUrl(string calldata _url) onlyOwner external {
        ViaOracleUrl = _url;
    }   

    function setFiatPayoutUrl(string calldata _url) onlyOwner external {
        FiatPayoutUrl = _url;
    }

    function setCryptoDataURL(string calldata _url, bytes32 _fromCurrency, bytes32 _toCurrency) onlyManager(msg.sender) external {
        CryptoDataUrl[_fromCurrency][_toCurrency] = _url;
    }

    function setOracles(address[] calldata _oracles) onlyOwner external {
        oracles = _oracles;
    }

    function setBondTerm(address _bondToken, uint256 _term) external {
        require(ViaToken(_bondToken).getIssuer()==msg.sender, "Only issuer can set bond term");
        term[_bondToken] = _term;
    }

    function setClientAddress(address _client) onlyOwner external {
        client = _client;
    }

    function supportTokens(bytes32 _currency, address _address) onlyManager(msg.sender) external {
        if(currencyIndex[_currency]==0){
            currencies.push(_currency);
            currencyIndex[_currency] = currencies.length;
            currencyAddress[_currency] = _address;
        }
    }

    function checkSupportForToken(bytes32 _currency) external view returns(bool){
        if(currencyIndex[_currency]>0)
            return true;
        else
            return false;
    }

    function getCurrencyToken(bytes32 _currency) external view returns(address){
        return currencyAddress[_currency];
    }

}
