pragma solidity 0.5.7;

import "./interfaces/ViaBond.sol";
import "./interfaces/ViaToken.sol";
import "./interfaces/ViaFactory.sol";

import "./utilities/ERC20.sol";

contract Token is ViaToken, ERC20 {

    //Via token attributes (eg, name : Via-USD, product : ViaBond, symbol : bond identifier)
    address private _issuer;
    address private _issuingContract;
    ViaFactory private _factory;

    //initiliaze proxies
    function initialize(bytes32 name, address issuer, address owner, bytes32 symbol) public initializer{
        _factory = ViaFactory(msg.sender);
        _issuer = issuer;
        _issuingContract = owner;
        setName(string(abi.encodePacked(name)));
        setSymbol(string(abi.encodePacked(symbol)));
    }

    function getIssuer() external view returns(address){
        return _issuer;
    }
    
    function addTotalSupply(uint256 amount) external{
        require(_issuingContract==msg.sender, "Invalid requestor");
        _mint(address(this), amount);
    }

    function reduceSupply(uint256 amount) external{        
        require(_issuingContract==msg.sender, "Invalid requestor");
        _burn(address(this), amount);
    }

    function addBalance(address party, uint256 amount) external{
        require(_issuingContract==msg.sender, "Invalid requestor");
        _mint(party, amount);
    }

    function reduceBalance(address party, uint256 amount) external{
        require(_issuingContract==msg.sender, "Invalid requestor");
        _burn(party, amount);
    }

    function getBalance(address party) external returns(uint256){
        require(_issuingContract==msg.sender, "Invalid requestor");
        return balanceOf(party);
    }

    function transferFrom(address sender, address receiver, uint256 tokens) public returns (bool){
        //ensure sender has enough tokens in balance before transferring or redeeming them
        require(balanceOf(sender) >= tokens);
        if(ViaBond(_issuingContract).transferForward(sender, receiver, tokens, address(this)))
            return true;
        else
            return false;
    }

    function transferToken(address sender, address receiver, uint256 tokens) external returns (bool){
        require(_issuingContract==msg.sender, "Invalid requestor");
        //owner should have more tokens than being transferred
        if(balanceOf(sender) >= tokens){
            //sending contract should be allowed by token owner to make this transfer
            _transfer(sender, receiver, tokens);
            return true;
        }
        return false;
    }   

    function requestTransfer(address receiver, uint256 tokens) external returns (bool){
        require(_issuingContract==msg.sender, "Invalid requestor");
        if(transfer(receiver, tokens))
            return true;
        else
            return false;
    }  

    function requestTransaction(uint256 amount, address payer, bytes32 currency, address cashContract) external returns(bool){
        require(_factory.getType(msg.sender) == "ViaCash" || _factory.checkSupportForToken(currency), "Unsupported token");
        if(payer==_issuer){
            if(ViaBond(_issuingContract).requestRedemption(amount, payer, currency, address(this)))
                return true;
            else
                return false;
        }
        else{
            if(ViaBond(_issuingContract).requestPurchase(amount, payer, currency, cashContract))
                return true;
            else
                return false;
        }        
    }  
    
}
