pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "./interfaces/VerifiedClient.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/ownership/Ownable.sol";

contract Client is VerifiedClient, Initializable, Ownable {
    
    //bridge
    address private bridge;

    struct role{
        address manager;
        bytes32 country;
        bytes32 role;
        bytes32 id;
    }

    struct user{
        bytes32 name;
        bytes32 accountid;
        uint256 kycstatus; //3 for pass, 1 for pending
        bytes32 country;
        uint256 amlscore;
        uint256 creditScore;
        bytes32 contact;        
    }    

    struct kyc{
        string identity;
        string videokyc;
        string docs;
    }

    // AMLA score to pass
    uint256 private passScore;

    //mapping of client address to user attributes
    mapping(address => user) private clients;

    mapping(address => kyc) private kycDocs;

    //storing users for each country
    mapping(bytes32 => address[]) private usersbyCountry;

    //mapping of client to manager
    mapping(address => address) private managers;

    //mapping of a manager to its sub managers
    mapping(address=> role) private roles;

    //storing submanagers
    address[] private submanagers;

    //submanager index
    mapping(address => uint256) private managerIndex;

    //custody account for managers
    mapping(address => custodian[]) private accounts;

    //custody account for users
    mapping(address => bytes32) private custodyAccount;

    event ManagerAdded(address manager, address indexed submanager, bytes32 role, bytes32 country, bytes32 managerId);
    event ManagerRemoved(address manager, address indexed submanager, bytes32 role, bytes32 country);
    event UserAdded(address client, bytes32 name, bytes32 accountid, bytes32 country, uint status);

    function initialize(address _signer) public initializer{
        Ownable.initialize(msg.sender);
        bridge = _signer;
    }

    function setSigner(address _signer) external onlyOwner {
        bridge = _signer;
    }
    
    function addRole(address _manager, address _submanager, bytes32 _country, bytes32 _role, bytes32 _id, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== owner() || msg.sender==bridge, "Unauthorized access");
        require(_role=="Custodian" || _role=="AM" || _role=="DP" || _role=="KYCAML" || _role=="Issuer", "Invalid manager type");
        require(_submanager!=owner(), "Incorrect role addition");
        bool found = false;
        uint length = submanagers.length;
        for(uint256 i=0; i<length; i++){
            if(submanagers[i]==_submanager)
                found = true;
        }
        if(!found){
            submanagers.push(_submanager);
            managerIndex[_submanager] = submanagers.length;
            roles[_submanager].manager = _manager;
            roles[_submanager].country = _country;
            roles[_submanager].role = _role;
            roles[_submanager].id = _id;
            if(_role=="KYCAML")
                assignManagerToUser(_submanager, _country);
            emit ManagerAdded(_manager, _submanager, _role, _country, _id);
        }
    }

    function getRole(address _user) external view returns(bytes32, bytes32, bytes32){
        uint256 position = managerIndex[_user];
        if(position>0){
            if(submanagers[position-1]==_user){
                return (roles[submanagers[position-1]].role, roles[submanagers[position-1]].id, roles[submanagers[position-1]].country);
            }
        }
        if(_user==owner())
            return ("Admin","","");
        else
            return ("","","");
    }

    function removeRole(address _manager, address _submanager, bytes32 _country, bytes32 _role, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) 
                            external {
        bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(ecrecover(messageHash, _v, _r, _s)== owner() || msg.sender==bridge, "Unauthorized access");
        require(_role=="Custodian" || _role=="AM" || _role=="DP" || _role=="KYCAML" || _role=="Issuer", "Invalid manager type");
        uint256 position = managerIndex[_submanager];
        if(position>0){
            if( submanagers[position-1]==_submanager &&
                roles[submanagers[position-1]].role==_role && 
                roles[submanagers[position-1]].country==_country &&
                roles[submanagers[position-1]].manager==_manager){                
                    roles[submanagers[position-1]].manager = address(0x0);
                    roles[submanagers[position-1]].country = "";
                    roles[submanagers[position-1]].role = "";
                    roles[submanagers[position-1]].id = "";
                    delete(submanagers[position-1]);
            }
        }
        if(_role=="KYCAML")
            unassignManagerFromUser(_submanager, _country);
        emit ManagerRemoved(_manager, _submanager, _role, _country);
    }   

    function setAmlScore(address client, uint256 score) external {
        require(bridge==msg.sender || managers[client]==msg.sender, "Unauthorized access");
        clients[client].amlscore = score;
    }

    function setAmlPassScore(uint256 _score) external {
        require(bridge==msg.sender, "Unauthorized access");
        passScore = _score;
    }   

    function getAMLStatus(address client) external view returns(bool){
        if(clients[client].amlscore!=0)
            return clients[client].amlscore >= passScore ? true : false;
        else
            return true;
    }

    function setCreditScore(address client, uint256 score) external {
        require(bridge==msg.sender || managers[client]==msg.sender, "Unauthorized access");
        clients[client].amlscore = score;
    }

    function KycUpdate(address client, bytes32 name, bytes32 accountid, bytes32 country, bytes32 contact, uint status) external { //, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s) external {
        //bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        //bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(/*ecrecover(messageHash, _v, _r, _s)== bridge*/ msg.sender==bridge || managers[client]==msg.sender || roles[msg.sender].role=="KYCAML" /*|| msg.sender==bridge ||roles[msg.sender].country==clients[client].country*/, "Unauthorized access");
        clients[client].name = name;
        clients[client].accountid = accountid;
        clients[client].kycstatus = status;
        clients[client].country = country;
        clients[client].contact = contact;
        usersbyCountry[country].push(client);
        matchManagerToClient(client, country);
        emit UserAdded(client, name, accountid, country, status);
    }

    function getClientKYC(address client) external view returns(bytes32, bytes32, bytes32, uint256, bytes32, uint256, uint256){
        require(msg.sender==owner() || msg.sender==client || managers[client]==msg.sender || roles[msg.sender].role=="KYCAML" 
                || roles[msg.sender].role=="AM" || roles[msg.sender].role=="DP" || roles[msg.sender].role=="Issuer" || roles[msg.sender].role=="Custodian", "Unauthorized access to check KYC");
        return (clients[client].name, 
                clients[client].accountid, 
                clients[client].country, 
                clients[client].kycstatus, 
                clients[client].contact,
                clients[client].creditScore,
                clients[client].amlscore);
    }

    function fullKycUpdate(address client, string calldata identity, string calldata videokyc, string calldata docs/*, bytes32 _hashedMessage, uint8 _v, bytes32 _r, bytes32 _s*/) external {
        //bytes32 payloadHash = keccak256(abi.encode(_hashedMessage, "L2toL1"));
        //bytes32 messageHash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash));
        require(/*ecrecover(messageHash, _v, _r, _s)== bridge ||*/ msg.sender==bridge || managers[client]==msg.sender || roles[msg.sender].role=="KYCAML"/*||roles[msg.sender].country==clients[client].country*/, "Unauthorized access");
        kycDocs[client].identity = identity;
        kycDocs[client].videokyc = videokyc;
        kycDocs[client].docs = docs;
    }

    function getFullClientKYC(address client) external view returns(bytes32, string memory, string memory, string memory){
        require(msg.sender==owner() || msg.sender==client || managers[client]==msg.sender || roles[msg.sender].role=="KYCAML", "Unauthorized access to check KYC");
        return (
                clients[client].contact,
                kycDocs[client].identity,
                kycDocs[client].videokyc,
                kycDocs[client].docs
            );
    } 

    function matchManagerToClient(address client, bytes32 country) private {
        uint length = submanagers.length;
        for(uint256 i=0; i<length; i++){
            if(roles[submanagers[i]].country==country && roles[submanagers[i]].role=="KYCAML"){
                managers[client] = submanagers[i];
            }
        }
    }

    function unassignManagerFromUser(address _submanager, bytes32 _country) private {
        uint length = usersbyCountry[_country].length;
        for(uint256 i=0; i<length; i++){
            if(managers[usersbyCountry[_country][i]]==_submanager)
                managers[usersbyCountry[_country][i]] = address(0x0);
        }
    }

    function assignManagerToUser(address _submanager, bytes32 _country) private {
        uint length = usersbyCountry[_country].length;
        for(uint256 i=0; i<length; i++){
            if(managers[usersbyCountry[_country][i]]==address(0x0))
                managers[usersbyCountry[_country][i]] = _submanager;
        }
    }

    /**
        Gets DP id
        @param  party         reference to the party or counterparty in a trade
     */
    function getTransferAgent(address party) external view returns(bytes32){
        return roles[party].id;
    }

    function getManagers(bytes32 _country, bytes32 _role) external view returns(address[] memory){
        address[] memory sm = new address[](submanagers.length);
        uint256 j=0;
        uint length = submanagers.length;
        for(uint256 i=0; i<length; i++){
            if(roles[submanagers[i]].country==_country && roles[submanagers[i]].role==_role){
                sm[j] = submanagers[i];
                j=j+1;
            }
        }
        return sm;
    }

    function setCustodyAccount(address _submanager, bytes32 _currency, bytes32 _accountId) external {
        require(msg.sender==_submanager, "Unauthorized access");
        bool found = false;
        if(roles[_submanager].country!=""){
            uint length = accounts[_submanager].length;
            for(uint256 i=0; i<length; i++){
                if(accounts[_submanager][i].currency==_currency){
                    accounts[_submanager][i].account = _accountId;
                    found = true;
                }
            }
        }
        if(!found){
            VerifiedClient.custodian memory c = VerifiedClient.custodian({
                currency : _currency,
                account : _accountId
            });
            accounts[_submanager].push(c);
        }            
    }

    function getCustodyAccount(address _submanager) external view returns(custodian[] memory){
        return accounts[_submanager];
    }

    function setCustody(address userAddress, bytes32 accountId) external{
        require(msg.sender==userAddress || msg.sender==bridge, "Unauthorized access");
        custodyAccount[userAddress] = accountId;
    }

    function getCustody(address userAddress) external view returns(bytes32){
        return custodyAccount[userAddress];
    }

}
