// A simple ERC20 based security token
// (c) Kallol Borah, 2021

pragma solidity 0.5.7;
pragma experimental ABIEncoderV2;

import "../interfaces/ISecurity.sol";
import "../interfaces/VerifiedClient.sol";
import "@openzeppelin/upgrades/contracts/Initializable.sol";
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20.sol";

contract Security is Initializable, ERC20 {

    string public name;
    string public symbol;

    address preTradeContract;
    address tradeContract;
    address postTradeContract;
    address clientContract;

    // mapping security token holders to tokens held
    mapping(address => ISecurity.registry[]) private SettlementRegistry;

    address[] private holders;

    function initialize(bytes32 _company, 
                        bytes32 _isin, 
                        address _preTradeContract, 
                        address _tradeContract, 
                        address _postTradeContract, 
                        address _clientContract
                        ) public initializer {
        name = string(abi.encodePacked(_company, _isin));
        symbol = string(abi.encodePacked(_isin));
        preTradeContract = _preTradeContract;
        tradeContract = _tradeContract;
        postTradeContract = _postTradeContract;
        clientContract = _clientContract;
    }

    function transferFrom(address _transferor, address _transferee, uint256 _qty) public returns(bool) {
        require(msg.sender==tradeContract || msg.sender==postTradeContract);
        bool found = false;
        for(uint i=0; i<holders.length; i++){
            if(holders[i]==_transferee)
                found = true;
        }
        if(!found)
            holders.push(_transferee);
        //uint size = SettlementRegistry[_transferee].length;
        ISecurity.registry memory r1 = ISecurity.registry({
            transferee : _transferee,
            transferor : _transferor,
            amount : _qty,
            transferDateTime : now
        }); 
        SettlementRegistry[_transferee].push(r1);
        ISecurity.registry memory r2 = ISecurity.registry({
            transferee : _transferee,
            transferor : _transferor,
            amount : _qty,
            transferDateTime : now
        }); 
        SettlementRegistry[_transferor].push(r2);
        _transfer(_transferor, _transferee, _qty); 
        return true;
    }

    function mint(address _account, uint256 _amount) external {
        require(msg.sender==preTradeContract);
        bool found = false;
        for(uint i=0; i<holders.length; i++){
            if(holders[i]==_account)
                found = true;
        }
        if(!found)
            holders.push(_account);
        ISecurity.registry memory r = ISecurity.registry({
            transferee : _account,
            transferor : address(this),
            amount : _amount,
            transferDateTime : now
        }); 
        SettlementRegistry[_account].push(r);
        _mint(_account, _amount);
    }

    function getSettlements(address _holder) external view returns(ISecurity.registry[] memory){
        require(_holder==msg.sender || VerifiedClient(clientContract).getManager(_holder)==msg.sender);
        return (SettlementRegistry[_holder]);
    }

}
